mapper.update:
INSERT INTO T_COM_PLAN 
            (
            	 PLAN_NUM	
             	,VERSION_SEQ	
             	,CUSTOMER_NUM
             	,PLAN_NAME	
             	,CREATE_DATE	
             	,START_DATE
             	,PLAN_TYPE
             	,BASE_PLAN_NUM
            ) 
VALUES      (
				 #{PLAN_NUM			}		
             	,#{VERSION_SEQ		}	
             	,#{CUSTOMER_NUM		}	
             	,#{PLAN_NAME		}	
             	,now()			
             	,#{START_DATE		}
             	,#{PLAN_TYPE     	}
             	,#{BASE_PLAN_NUM 	}
            )  
ON DUPLICATE KEY 
UPDATE  PLAN_NAME		= #{PLAN_NAME		}     
       ,PLAN_TYPE		= #{PLAN_TYPE		}     
       ,BASE_PLAN_NUM	= #{BASE_PLAN_NUM	}     
       ,START_DATE		= #{START_DATE		}

