mapper.update:
INSERT INTO T_FIN_INSURANCE 
            (
				 INSURANCE_NUM
				,INSURANCE_TYPE
				,COMPANY_NAME
				,TITLE
				,CONTRACTOR
				,PAY_EACH_MONTH
				,START_DATE
				,GUARANTEE_TERM
				,GUARANTEE_TERM_TYPE
				,PAY_TERM
				,PAY_TERM_TYPE
				,COMMENT
				,STATE
				,CREATE_DATE
            ) 
VALUES      (
				 #{INSURANCE_NUM         }
				,#{INSURANCE_TYPE        }
				,#{COMPANY_NAME          }
				,#{TITLE                 }
				,#{CONTRACTOR            }
				,#{PAY_EACH_MONTH        }
				,#{START_DATE            }
				,#{GUARANTEE_TERM        }
				,#{GUARANTEE_TERM_TYPE   }
				,#{PAY_TERM              }
				,#{PAY_TERM_TYPE         }
				,#{COMMENT               }
				,#{STATE               	 }
				,now()
            )  
ON DUPLICATE KEY 
UPDATE 
				 INSURANCE_NUM			= #{INSURANCE_NUM         } 
				,INSURANCE_TYPE         = #{INSURANCE_TYPE        } 
				,COMPANY_NAME           = #{COMPANY_NAME          } 
				,TITLE                  = #{TITLE                 } 
				,CONTRACTOR             = #{CONTRACTOR            } 
				,PAY_EACH_MONTH         = #{PAY_EACH_MONTH        } 
				,START_DATE             = #{START_DATE            } 
				,GUARANTEE_TERM         = #{GUARANTEE_TERM        } 
				,GUARANTEE_TERM_TYPE    = #{GUARANTEE_TERM_TYPE   } 
				,PAY_TERM               = #{PAY_TERM              } 
				,PAY_TERM_TYPE          = #{PAY_TERM_TYPE         } 
				,COMMENT                = #{COMMENT               } 
				,STATE                	= #{STATE                 }

DELETE FROM T_FIN_INSURANCE_DAMBO
WHERE 1=1
	AND INSURANCE_NUM 	= #{INSURANCE_NUM      }
	AND INSURED_NUM 	= #{INSURED_NUM        }

mapper.select:
SELECT FID.INSURANCE_DAMBO_NUM ,FID.INSURANCE_MONEY ,FID.INSURANCE_NUM ,FID.INSURED_NUM ,FID.DAMBO_NUM ,FI.INSURED_NAME FROM T_FIN_INSURED FI ,T_FIN_INSURANCE_DAMBO FID WHERE 1=1 AND FI.INSURANCE_NUM = FID.INSURANCE_NUM AND FI.INSURED_NUM = FID.INSURED_NUM AND FID.INSURANCE_NUM = #{INSURANCE_NUM }
AND FID.INSURED_NUM = #{INSURED_NUM }
AND VALIDITY = 'Y'

mapper.update:
		INSERT INTO T_FIN_DAMBO 
        (
			 DAMBO_NUM
			,CATEGORY_NUM
			,DAMBO_NAME
			,USER_ID
        ) 
		VALUES      
		(
			 #{DAMBO_NUM   }
			,#{CATEGORY_NUM}
			,#{DAMBO_NAME  }
			,#{USER_ID     }
        )
		ON DUPLICATE KEY 
		UPDATE 
			 CATEGORY_NUM		= #{CATEGORY_NUM} 
			,DAMBO_NAME			= #{DAMBO_NAME}

DELETE FROM T_FIN_DAMBO_GROUP

		USER_ID 	= #{USER_ID}
	AND GROUP_NUM 	= #{GROUP_NUM}

		INSERT INTO T_FIN_DAMBO_GROUP_INFO 
        (
        	 USER_ID
			,GROUP_NUM
			,GROUP_NAME
		  	,BASE_CHECK    
		  	,CHART_CHECK    
        ) 
		VALUES      
		(
			 #{USER_ID   }
			,#{GROUP_NUM }
			,#{GROUP_NAME}
			,#{BASE_CHECK}
			,#{CHART_CHECK}
        )
		ON DUPLICATE KEY 
		UPDATE 
			  GROUP_NAME	= #{GROUP_NAME} 
			 ,BASE_CHECK	= #{BASE_CHECK} 
			 ,CHART_CHECK	= #{CHART_CHECK}
